---
import Layout from '../layouts/Layout.astro';
---

<Layout title="Login">
  <div class="min-h-screen flex items-center justify-center">
    <div class="card bg-base-100 shadow-xl w-full max-w-md">
      <div class="card-body items-center text-center">
        <h1 class="card-title text-2xl mb-4">Sign In</h1>
        <p class="text-base-content/70 mb-6">Sign in to access the file uploader</p>
        
        <button id="google-signin" class="btn btn-primary gap-2 w-full">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" class="w-5 h-5 fill-current">
            <path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" fill="#4285F4"/>
            <path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" fill="#34A853"/>
            <path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" fill="#FBBC05"/>
            <path d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" fill="#EA4335"/>
          </svg>
          Sign in with Google
        </button>
        
        <div id="error-message" class="text-error mt-4 hidden"></div>
      </div>
    </div>
  </div>
</Layout>

<script>
  import { authClient } from '../lib/auth-client';

  const googleBtn = document.getElementById('google-signin');
  const errorMsg = document.getElementById('error-message');

  googleBtn?.addEventListener('click', async () => {
    try {
      googleBtn.classList.add('loading');
      
      await authClient.signIn.social({
        provider: 'google',
        callbackURL: '/ca/',
      });
    } catch (error) {
      console.error('Sign in error:', error);
      if (errorMsg) {
        errorMsg.textContent = 'Sign in failed. Please try again.';
        errorMsg.classList.remove('hidden');
      }
    } finally {
      googleBtn.classList.remove('loading');
    }
  });
</script>
