commits.content:
---
import Layout from '../layouts/Layout.astro';

// If already logged in, redirect to home
if (Astro.locals.session) {
  return Astro.redirect('/ca/');
}
---

<Layout title="Login">
  <div class="min-h-screen flex items-center justify-center bg-base-200">
    <div class="card w-96 bg-base-100 shadow-xl">
      <div class="card-body">
        <h2 class="card-title text-2xl justify-center mb-6">Sign In</h2>
        
        <p class="text-center text-base-content/70 mb-6">
          Sign in to access the file uploader
        </p>
        
        <button 
          id="googleSignIn" 
          class="btn btn-primary w-full gap-2"
        >
          <svg xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" viewBox="0 0 24 24" fill="currentColor">
            <path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" fill="#4285F4"/>
            <path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" fill="#34A853"/>
            <path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" fill="#FBBC05"/>
            <path d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" fill="#EA4335"/>
          </svg>
          Sign in with Google
        </button>
        
        <div id="errorMessage" class="alert alert-error mt-4 hidden">
          <span id="errorText"></span>
        </div>
      </div>
    </div>
  </div>
</Layout>

<script>
  import { authClient } from '../lib/auth-client';

  const googleSignInBtn = document.getElementById('googleSignIn') as HTMLButtonElement;
  const errorMessage = document.getElementById('errorMessage') as HTMLDivElement;
  const errorText = document.getElementById('errorText') as HTMLSpanElement;

  googleSignInBtn.addEventListener('click', async () => {
    googleSignInBtn.disabled = true;
    googleSignInBtn.classList.add('loading');
    
    try {
      await authClient.signIn.social({
        provider: 'google',
        callbackURL: '/ca/',
      });
    } catch (error) {
      console.error('Sign in error:', error);
      errorMessage.classList.remove('hidden');
      errorText.textContent = error instanceof Error ? error.message : 'Sign in failed';
      googleSignInBtn.disabled = false;
      googleSignInBtn.classList.remove('loading');
    }
  });
</script>
